import React, { useState } from 'react';
import './App.css';
import { getList } from './Services/ListService'

function App() {
  state = {
    list: [],
    numberOfItems: 0
  }
  const list = () => {
    getList()
      .then(items => {
        console.log(items)
        this.setState({list: items, numberOfItems: items.length})
      });
  }
  const getItemComponents = (items) => {
    console.log(items)
    return items.map((item) => {
      return (
        <li>{item}</li>
      )
    })
  }
  return (
    <div className="GroceryList">
      <header className="Header">
        <h1>Grocery List</h1>
      </header>
      <div className="MainContent">
        <ul className="List">
          {getItemComponents(list)}
        </ul>
      </div>
    </div>
  );
}

export default App;
